// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract CodexAchievements {
    string public constant index = "Achievements";

    function achievement_by_id(uint _id)
        external
        pure
        returns (
            uint id,
            string memory name,
            uint attribute_id,
            uint synergy,
            bool retry,
            bool armor_check_penalty,
            string memory check,
            string memory action
        )
    {
        if (_id == 1) {
            return walkthourgh_chapter0();
        } else if (_id == 2) {
            return wander();
        } else if (_id == 3) {
            return wander();
        }
    }

    function walkthourgh_chapter0()
        public
        pure
        returns (
            uint id,
            string memory name,
            uint attribute_id,
            uint synergy,
            bool retry,
            bool armor_check_penalty,
            string memory check,
            string memory action
        )
    {
        id = 1;
        name = "Chapter 0 Finished";
        attribute_id = 4;
        synergy = 6; // craft
        retry = false;
        armor_check_penalty = false;
        check = "Complete find the door event";
        action = "Complete";
    }

    function wander()
        public
        pure
        returns (
            uint id,
            string memory name,
            uint attribute_id,
            uint synergy,
            bool retry,
            bool armor_check_penalty,
            string memory check,
            string memory action
        )
    {
        id = 2;
        name = "Wander";
        attribute_id = 4;
        synergy = 6; // craft
        retry = false;
        armor_check_penalty = false;
        check = "wander 10 times";
        action = "Wander";
    }
}

contracts/codex/CodexBaseRandom.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract CodexBaseRandom {
    string public constant index = "Base";
    string public constant class = "Random";

    function d100(uint _summoner) external view returns (uint) {
        return dn(_summoner, 100);
    }

    function d20(uint _summoner) external view returns (uint) {
        return dn(_summoner, 20);
    }

    function d12(uint _summoner) external view returns (uint) {
        return dn(_summoner, 12);
    }

    function d10(uint _summoner) external view returns (uint) {
        return dn(_summoner, 10);
    }

    function d8(uint _summoner) external view returns (uint) {
        return dn(_summoner, 8);
    }

    function d6(uint _summoner) external view returns (uint) {
        return dn(_summoner, 6);
    }

    function d4(uint _summoner) external view returns (uint) {
        return dn(_summoner, 4);
    }

    function dn(uint _summoner, uint _number) public view returns (uint) {
        return _seed(_summoner) % _number;
    }

    function _random(string memory input) internal pure returns (uint256) {
        return uint256(keccak256(abi.encodePacked(input)));
    }

    function _seed(uint _summoner) internal view returns (uint rand) {
        rand = _random(
            string(
                abi.encodePacked(
                    block.timestamp,
                    blockhash(block.number - 1),
                    _summoner,
                    msg.sender
                )
            )
        );
    }
}

