// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract CodexEvents {
    string public constant index = "Skills";

    function event_by_id(uint _id)
        external
        pure
        returns (
            uint id,
            string memory name,
            bool retry,
            string memory text,
            string memory traits_type_check,
            uint event_dependency,
            string memory success_text,
            string memory failure_text
        )
    {
        if (_id == 1) {
            return find_the_door();
        } else if (_id == 2) {
            return find_key();
        } else if (_id == 3) {}
    }

    function find_the_door()
        public
        pure
        returns (
            uint id,
            string memory name,
            bool retry,
            string memory text,
            string memory traits_type_check,
            uint event_dependency,
            string memory success_text,
            string memory failure_text
        )
    {
        id = 1;
        name = "Find the door";
        retry = false;
        text = "You find a door named 'Chapert 0' on this island";
        traits_type_check = "";
        event_dependency = 2;
        success_text = "You success open the door";
        failure_text = "The door is locked, you need find someting to open it.";
    }

    function find_key()
        public
        pure
        returns (
            uint id,
            string memory name,
            bool retry,
            string memory text,
            string memory traits_type_check,
            uint event_dependency,
            string memory success_text,
            string memory failure_text
        )
    {
        id = 2;
        name = "Find the key";
        retry = false;
        text = "You find a locked box. The password hint is time.";
        traits_type_check = "4:20 watch";
        event_dependency = 0;
        success_text = "You hava watch in your traits. You type in current time as password and get a golden key.";
        failure_text = "The box is locked, you need to find the password.";
    }
}

contracts/core/Achievements.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

interface Character {
    function level(uint) external view returns (uint);

    function getApproved(uint) external view returns (address);

    function ownerOf(uint) external view returns (address);
}

interface CodexAchievement {
    function achievement_by_id(uint)
        external
        view
        returns (
            uint id,
            string memory name,
            uint attribute_id,
            uint synergy,
            bool retry,
            bool armor_check_penalty,
            string memory check,
            string memory action
        );
}

contract Achievements {
    //----------------need fix the address--------------------
    Character constant _hunter =
        Character(0xce761D788DF608BD21bdd59d6f4B54b2e27F25Bb);
    CodexAchievement constant _codex_achievements =
        CodexAchievement(0x67ae39a2Ee91D7258a86CD901B17527e19E493B3);

    //---------------------------------------------------------

    mapping(uint => uint8[36]) public achievements;

    function set_achievements(uint _summoner, uint8[36] memory _achievements)
        external
    {
        require(_isApprovedOrOwner(_summoner));
        uint8[36] memory _current_achievements = achievements[_summoner];
        for (uint i = 0; i < 36; i++) {
            require(_current_achievements[i] <= _achievements[i]);
        }
        achievements[_summoner] = _achievements;
    }

    function get_achievements(uint _summoner)
        external
        view
        returns (uint8[36] memory)
    {
        return achievements[_summoner];
    }

    function _isApprovedOrOwner(uint _summoner) internal view returns (bool) {
        return
            _hunter.getApproved(_summoner) == msg.sender ||
            _hunter.ownerOf(_summoner) == msg.sender;
    }
}

contracts/core/Character.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract Character is IERC721, ERC721URIStorage {
    using Counters for Counters.Counter;

    //Variable
    Counters.Counter private _tokenIdCounter;
    mapping(address => bool) public minted_address;
    mapping(uint => string) public name;
    mapping(uint => uint) public level;
    mapping(uint => uint) public xp;
    // mapping(uint => uint) public coins;

    //Event
    event summoned(address indexed owner, uint hunter);
    event leveled(address indexed owner, uint level, uint hunter);
    event named(address indexed owner, uint name, uint hunter);

    constructor() ERC721("Character", "HUNTER") {}

    function summon() external {
        require(minted_address[msg.sender] == false, "Already summoned");

        uint _next_hunter = _tokenIdCounter.current();
        name[_next_hunter] = Strings.toHexString(msg.sender);
        level[_next_hunter] = 1;
        minted_address[msg.sender] = true;

        _safeMint(msg.sender, _next_hunter);
        emit summoned(msg.sender, _next_hunter);
        _tokenIdCounter.increment();
    }

    function level_up(uint _hunter) external {
        require(_isApprovedOrOwner(msg.sender, _hunter));
        uint _level = level[_hunter];
        uint _xp_required = xp_required(_level);
        xp[_hunter] -= _xp_required;
        level[_hunter] = _level + 1;
        emit leveled(msg.sender, level[_hunter], _hunter);
    }

    function change_name(uint _hunter, string memory _name) external {
        require(_isApprovedOrOwner(msg.sender, _hunter));
        name[_hunter] = _name;
        emit named(msg.sender, level[_hunter], _hunter);
    }

    function spend_xp(uint _hunter, uint _xp) external {
        require(_isApprovedOrOwner(msg.sender, _hunter));
        xp[_hunter] -= _xp;
    }

    function xp_required(uint curent_level)
        public
        pure
        returns (uint xp_to_next_level)
    {
        xp_to_next_level = curent_level * 1000e18;
        for (uint i = 1; i < curent_level; i++) {
            xp_to_next_level += curent_level * 1000e18;
        }
    }

    function hunter_info(uint _hunter)
        external
        view
        returns (
            string memory _name,
            uint _xp,
            uint _level
        )
    {
        _name = name[_hunter];
        _xp = xp[_hunter];
        _level = level[_hunter];
    }
}

